imports.generate_miseq_excel.wdl:
workflow generate_miseq_excel {

    String analysisName
    Array[File] lengthDistributionCsvs
    Array[File] statsCsvs

    call generate_excel {
        input:
            analysisName=analysisName,
            lengthDistributionCsvs=lengthDistributionCsvs,
            statsCsvs=statsCsvs
    }

    output {
        File excelWorkbook = generate_excel.excelWorkbook
    }
}


task generate_excel {

    String analysisName
    Array[File] lengthDistributionCsvs
    Array[File] statsCsvs

    command {
        mkdir data
        DATA_DIR=$(realpath data)

        for FILE in ${sep=' ' lengthDistributionCsvs}
        do
            cp $FILE data
        done

        for FILE in ${sep=' ' statsCsvs}
        do
            cp $FILE data
        done

        python /usr/src/python/grna_analysis/generate_excel.py -n "${analysisName}" -d "$DATA_DIR"
    }

    output {
        File excelWorkbook="${analysisName}.xlsx"
    }

    runtime {
        docker: "doejgi/synbioqc-libqc:0.2.2"
        poolname: "synbio_small"
        shared: 0
        node: 1
        nwpn: 5
        memory: "116G"
        time: "06:00:00"
    }
}

imports.miseq_library.wdl:
workflow miseq_library {

    String libName
    File libFastq
    File referencesFasta

    String flankSeq5P
    String flankSeq3P


    call flank_fasta as flank_seq_3P {
        input: flankSeq = flankSeq3P, seqId = "three_prime"
    }

    call flank_fasta as flank_seq_5P {
        input: flankSeq = flankSeq5P, seqId = "five_prime"
    }

    call extract_spacer_length {
        input: referencesFasta=referencesFasta
    }

    call bbmerge {
        input:
            libName=libName,
            libFastq=libFastq
    }

    call seal {
        input:
            mergedReads=bbmerge.mergedReads,
            threePrimeFlankFasta=flank_seq_3P.flankFasta,
            fivePrimeFlanksFasta=flank_seq_5P.flanksFasta,
            threePrimeRevcompFlankFasta=flank_seq_3P.revcompFlankFasta,
            length3PFlank=flank_seq_3P.flankLength,
            length5PFlank=flank_seq_5P.flankLength
    }

    call bbduk {
        input:
            libraryName=libName,
            spacerLength=extract_spacer_length.spacerLength,
            length3PFlank=flank_seq_3P.flankLength,
            length5PFlank=flank_seq_5P.flankLength,
            fivePrimeFlankFasta=flank_seq_5P.flankFasta,
            fivePrimeRevcompFlankFasta=flank_seq_5P.revcompFlankFasta,
            threePrimeFlankFasta=flank_seq_3P.flankFasta,
            threePrimeRevcompFlankFasta=flank_seq_3P.revcompFlankFasta,
            filteredReads=seal.out
    }

    if (extract_spacer_length.spacerLength > 31) {
        call stats_python {
            input:
                libraryName=libName,
                referencesFasta=referencesFasta,
                readsWithSpacersFastq=bbduk.readsWithSpacersFastq
        }
    }

    if (extract_spacer_length.spacerLength <= 31) {
        call stats_seal {
            input:
                libraryName=libName,
                spacerLength=extract_spacer_length.spacerLength,
                referencesFasta=referencesFasta,
                readsWithSpacersFastq=bbduk.readsWithSpacersFastq
        }
    }

    output {
        File? lengthDistributionCsv = bbduk.lengthDistributionCsv
        File? statsSealCsv = stats_seal.statsCsv
        File? statsPythonCsv = stats_python.statsCsv
    }

}


task extract_spacer_length {

    File referencesFasta

    command {
        python /usr/src/python/grna_analysis/extract_spacer_length.py ${referencesFasta}
    }

    output {
        Int spacerLength = read_int(stdout())
    }

    runtime {
        docker: "doejgi/synbioqc-libqc:0.2.2"
        poolname: "synbio_small"
        shared: 0
        node: 1
        nwpn: 5
        memory: "116G"
        time: "06:00:00"
    }
}

task flank_fasta {

    String flankSeq
    String seqId

    command {
        python /usr/src/python/grna_analysis/generate_flank_fasta.py -f ${flankSeq} -i "${seqId}" 1>&2

        FLANK_LENGTH=$(echo "${flankSeq}" | tr -d " \n" | wc -c)
        ADJUSTED_FLANK_LENGTH=$(($FLANK_LENGTH > 31 ? 31 : $FLANK_LENGTH))
        echo $ADJUSTED_FLANK_LENGTH
    }

    output {
        Int flankLength=read_int(stdout())
        File flankFasta='${seqId}_flank.fasta'
        File revcompFlankFasta='${seqId}_revcomp_flank.fasta'
        File flanksFasta='${seqId}_flanks.fasta'
    }

    runtime {
        docker: "doejgi/synbioqc-libqc:0.2.2"
        poolname: "synbio_small"
        shared: 0
        node: 1
        nwpn: 5
        memory: "116G"
        time: "06:00:00"
    }
}


task bbmerge {

    String libName
    File libFastq

    command {
        # #-Xmx23g threads=20
        bbmerge.sh in=${libFastq} out="${libName}_merged.fastq" outu="${libName}_unmerged.fastq" #-Xmx23g
    }

    output {
        File mergedReads="${libName}_merged.fastq"
        File unmergedReads="${libName}_unmerged.fastq"
    }

    runtime {
        docker: "jfroula/aligner-bbmap:2.0.1"
        poolname: "synbio_small"
        shared: 0
        node: 1
        nwpn: 5
        memory: "116G"
        time: "06:00:00"
    }
}

task seal {

    Int length3PFlank
    Int length5PFlank

    File mergedReads

    File threePrimeFlankFasta
    File threePrimeRevcompFlankFasta

    File fivePrimeFlanksFasta

    command {
        echo using length5PFlank: ${length5PFlank}
        echo using length3PFlank: ${length3PFlank}

        ## filter by 5' (forward and rev-comp)
        echo filtering by 5 prime forward and rev-comp
        seal.sh in=${mergedReads}  mm=f rcomp=f pattern=out_%.fq  k=${length5PFlank} ref=${fivePrimeFlanksFasta} #-Xmx23g

        ## filter by 3' (forward)
        if [ -f ./out_five_prime_flank.fq ]
        then
            echo filtering by 3 prime forward
            seal.sh in=./out_five_prime_flank.fq  mm=f rcomp=f pattern=out_%.fq k=${length3PFlank} ref=${threePrimeFlankFasta} #-Xmx23g
        else
            echo not filtering by 3 prime forward
        fi

        ## filter by 3' (revcomp)
        if [ -f ./out_five_prime_flank_revcomp.fq ]
        then
            echo filtering by 3 prime rev-comp
            seal.sh in=./out_five_prime_flank_revcomp.fq  mm=f rcomp=f pattern=out_%.fq k=${length3PFlank} ref=${threePrimeRevcompFlankFasta} #-Xmx23g
        else
            echo not filtering by 3 prime rev-comp
        fi
    }

    # https://github.com/openwdl/wdl/blob/main/versions/development/SPEC.md#file-directory-and-optional-outputs
    output {
        Array[File] out=glob('out_*')
    }

    runtime {
        docker: "jfroula/aligner-bbmap:2.0.1"
        poolname: "synbio_small"
        shared: 0
        node: 1
        nwpn: 5
        memory: "116G"
        time: "06:00:00"
    }
}

task bbduk {

    String libraryName

    Int spacerLength
    Int length3PFlank
    Int length5PFlank

    File fivePrimeFlankFasta

    File threePrimeFlankFasta
    File threePrimeRevcompFlankFasta
    File fivePrimeRevcompFlankFasta

    Array[File] filteredReads

    command {
        READS_WITH_5_PRIME_FLANK=reads_with_5_prime_flank.fastq
        READS_WITH_BOTH_FLANKS=reads_with_both_flanks.fastq

        READS_WITH_5_PRIME_FLANK_TRIMMED=reads_with_5_prime_flank_trimmed.fastq
        READS_WITH_BOTH_FLANKS_TRIMMED=reads_with_both_flanks_trimmed.fastq
        READS_WITH_SPACERS_FASTQ=reads_with_spacers.fastq

        TRIMMED_READS=trimmed_reads.fastq

        # link out_* files produced by earlier tasks into current directory
        for FILE in ${sep=' ' filteredReads}
        do
            echo linking $FILE
            ln -s $FILE .
        done


        ### cleanup

        ## reads w/ forward-oriented flanks
        if [ -f ./out_three_prime_flank.fq ]
        then
            bbduk.sh in=./out_three_prime_flank.fq out=./five_prime_forward_trim.fq ktrim=l k=${length3PFlank} mm=f ref=${fivePrimeFlankFasta} #-Xmx23g
            bbduk.sh in=./five_prime_forward_trim.fq out=./both_forward_trim.fq ktrim=r k=${length5PFlank} mm=f ref=${threePrimeFlankFasta} #-Xmx23g

            cat ./both_forward_trim.fq > $TRIMMED_READS
        fi

        ## reads w/ reverse complemented flanks
        if [ -f ./out_three_prime_flank_revcomp.fq ]
        then
            bbduk.sh in=./out_three_prime_flank_revcomp.fq out=./five_prime_revcomp_trim.fq ktrim=r k=${length5PFlank} mm=f ref=${fivePrimeRevcompFlankFasta} #-Xmx23g
            bbduk.sh in=./five_prime_revcomp_trim.fq out=./both_revcomp_trim.fq ktrim=l k=${length3PFlank} mm=f ref=${threePrimeRevcompFlankFasta} #-Xmx23g

            cat ./both_revcomp_trim.fq >> $TRIMMED_READS
        fi

        if [ -f $TRIMMED_READS ]
        then
            echo trimmed reads
            head -10 $TRIMMED_READS
            ## calculate the # of trimmed reads that are 20bp long
            bbduk.sh in=$TRIMMED_READS lhist=${libraryName}_length_distribution.csv out=$READS_WITH_SPACERS_FASTQ minlen=${spacerLength} maxlen=${spacerLength} int=f #-Xmx23g
            sed -i 's/\t/,/g' ${libraryName}_length_distribution.csv
        fi
    }

    output {
        File? trimmedReads = "trimmed_reads.fastq"
        File? readsWithSpacersFastq = "reads_with_spacers.fastq"
        File? lengthDistributionCsv = "${libraryName}_length_distribution.csv"
    }

    runtime {
        docker: "jfroula/aligner-bbmap:2.0.1"
        poolname: "synbio_small"
        shared: 0
        node: 1
        nwpn: 5
        memory: "116G"
        time: "06:00:00"
    }

}

task stats_seal {

    String libraryName
    Int spacerLength
    File referencesFasta
    File readsWithSpacersFastq

    command {
        seal.sh in=${readsWithSpacersFastq} stats=stats.csv ref=${referencesFasta} k=${spacerLength} mm=f nzo=f #-Xmx23g
        sed 's/\t/,/g' stats.csv > "${libraryName}_stats.csv"
    }

    runtime {
        docker: "jfroula/aligner-bbmap:2.0.1"
        poolname: "synbio_small"
        shared: 0
        node: 1
        nwpn: 5
        memory: "116G"
        time: "06:00:00"
    }

    output {
        File statsCsv = "${libraryName}_stats.csv"
    }

}

task stats_python {

    String libraryName
    File referencesFasta
    File readsWithSpacersFastq

    command {
        python /usr/src/python/grna_analysis/count_references_occurrences.py ${referencesFasta} ${readsWithSpacersFastq}
        sed 's/\t/,/g' stats.csv > "${libraryName}_stats.csv"
    }

    output {
        File statsCsv = "${libraryName}_stats.csv"
    }

    runtime {
        docker: "doejgi/synbioqc-libqc:0.2.2"
        poolname: "synbio_small"
        shared: 0
        node: 1
        nwpn: 5
        memory: "116G"
        time: "06:00:00"
    }
}

